DROP VIEW IF EXISTS `inventario`.`compradores`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `inventario`.`compradores` AS select `u`.`user_id` AS `user_id`,`u`.`login_name` AS `login_name`,`u`.`login_password` AS `login_password`,`u`.`nivel_visibilidad` AS `nivel_visibilidad`,`u`.`nombre_completo` AS `nombre_completo`,`u`.`email` AS `email`,`u`.`nro_legajo` AS `nro_legajo` from `infraestructura`.`website_user` `u` where `u`.`user_id` in (select `infraestructura`.`website_user`.`user_id` AS `user_id` from ((`infraestructura`.`website_user` join `infraestructura`.`usuario_roles` on((`infraestructura`.`website_user`.`user_id` = `infraestructura`.`usuario_roles`.`user_id`))) join `infraestructura`.`roles` `r` on((`infraestructura`.`usuario_roles`.`rol_id` = `r`.`rol_id`))) where (`r`.`nombre` like _latin1'COMPRADOR'));