-- Indiquese el valor del id del atributo ARTICULO_UNIDAD_MEDIDA 
-- con el correspondiente a la base de datos local en el where al 
-- final de la sentencia
DROP VIEW IF EXISTS `inventario`.`articulos_extendidos`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `inventario`.`articulos_extendidos` AS select `inventario`.`articulos`.`articulo_id` AS `articulo_id`,`inventario`.`articulos`.`clase_articulo_id` AS `clase_articulo_id`,`inventario`.`articulos`.`nombre` AS `nombre`,`inventario`.`articulos`.`descripcion` AS `descripcion`,`inventario`.`articulos`.`observaciones` AS `observaciones`,`inventario`.`articulos`.`descripcion_completa` AS `descripcion_completa`,`inventario`.`articulos`.`activo` AS `activo`,`inventario`.`articulos`.`anulado` AS `anulado`,`inventario`.`unidades_medida`.`nombre` AS `nombre_unidad` from ((`inventario`.`articulos` join `infraestructura`.`atributos_entidad` `atributos` on(((`atributos`.`tipo_objeto` = _latin1'TABLA') and (`atributos`.`nombre_objeto` = _latin1'articulos') and (`atributos`.`objeto_id` = `inventario`.`articulos`.`articulo_id`)))) join `inventario`.`unidades_medida` on((`inventario`.`unidades_medida`.`unidad_medida_id` = `atributos`.`valor`))) where (`atributos`.`atributo_id` = );